mapper.update:
UPDATE book_ex.tbl_reply 
SET	   replytext = #{replytext}
	 , updatedate = now()
WHERE rno = #{rno}

mapper.select:
SELECT
		*
FROM	book_ex.tbl_reply
WHERE 	bno = #{bno}
ORDER BY rno DESC
OFFSET #{cri.pageStart} LIMIT #{cri.perPageNum}

SELECT 	COUNT(bno)
FROM 	book_ex.tbl_reply
WHERE	bno = #{bno}
